mapper.select:
SELECT c.courier_id, c.employee_id, c.work_type, c.daily_distribution_parcels_num, t.truck_type, t.truck_plate_number, t.max_weight, t.storage_area_height, t.storage_area_width, t.storage_area_length, t.volume, e.full_name, e.employee_code, e.phone_number, e.avatar, e.work_city, r.role_type, p.company_name, p.company_type, a.account_id, a.username, a.email, a.password, a.status FROM t_41_courier AS c RIGHT JOIN t_72_company_employee AS e ON c.employee_id = e.employee_id LEFT JOIN t_81_role_type AS r ON e.role_id = r.role_id LEFT JOIN t_43_truck AS t ON c.truck_id = t.truck_id LEFT JOIN t_71_parcel_hub_company AS p ON e.company_id = p.company_id LEFT JOIN t_11_registered_account AS a ON e.account_id = a.account_id
AND a.username = #{username}
AND a.email = #{email}
